mapper.update:
UPDATE music 
SET m_youtube_id = #{m_youtube_id} 
WHERE m_no = #{m_no}

mapper.insert:
INSERT INTO history (
    h_no, u_no, m_no, h_location, h_weather, h_lat, h_lon, h_played_at
) VALUES (
    history_no_seq.NEXTVAL, #{u_no}, #{m_no}, #{h_location}, #{h_weather}, #{h_lat}, #{h_lon}, SYSDATE
)

mapper.select:
SELECT COUNT(*) FROM likes 
WHERE u_no = #{u_no} 
  AND l_target_type = 'MUSIC' 
  AND l_target_no = #{m_no}

mapper.delete:
DELETE FROM likes 
WHERE u_no = #{u_no} 
  AND l_target_type = 'MUSIC' 
  AND l_target_no = #{m_no}

mapper.select:
SELECT music_id as "m_no", danceability, energy, valence, acousticness, instrumentalness, liveness, speechiness, tempo
FROM music_feature 
WHERE music_id = #{m_no}

SELECT m_no, lyrics_text, lyrics_trans, lyricist, lyrics_sync
FROM music_lyrics
WHERE m_no = #{m_no}

SELECT m.m_no 
FROM music m
JOIN artists a ON m.a_no = a.a_no
WHERE m.m_title = #{title} 
  AND a.a_name = #{artist}

SELECT m_youtube_id 
FROM music 
WHERE m_no = #{m_no}
