mapper.update:
DECLARE
    target_u_no NUMBER;
BEGIN
    -- 1. 유저 번호 가져오기
    SELECT u_no INTO target_u_no FROM "USER" WHERE u_id = #{uId};

    -- 2. [필수] 손자 레코드 삭제: 구독 번호(s_no)를 참조하는 결제 로그 먼저 삭제
    DELETE FROM payment_logs 
    WHERE s_no IN (SELECT s_no FROM subscriptions WHERE u_no = target_u_no);

    -- 3. [필수] 자식 레코드 삭제: 유저를 직접 참조하는 테이블들
    DELETE FROM subscriptions WHERE u_no = target_u_no;
    DELETE FROM likes WHERE u_no = target_u_no;

    -- 4. 이전 처리 (데이터 보존): 유령 계정(0번)으로 업데이트
    UPDATE history SET u_no = 0 WHERE u_no = target_u_no;
    UPDATE reviews SET u_no = 0 WHERE u_no = target_u_no;
    UPDATE playlists SET u_no = 0 WHERE u_no = target_u_no;
    UPDATE inquiries SET u_no = 0 WHERE u_no = target_u_no;
    UPDATE notifications SET u_no = 0 WHERE u_no = target_u_no;

    -- 5. [선택] 플레이리스트 트랙은 플레이리스트가 업데이트되었으므로 
    -- 필요하다면 추가 삭제 처리를 할 수 있지만, 보통 플레이리스트를 0번으로 보냈으니 유지됩니다.

    -- 6. 마지막 부모 삭제
    DELETE FROM "USER" WHERE u_no = target_u_no;

EXCEPTION
    WHEN NO_DATA_FOUND THEN
        NULL;
    WHEN OTHERS THEN
        RAISE; -- 다른 에러 발생 시 로그 확인을 위해 추가
END;
